import Card from '@material-ui/core/Card';
import React from 'react';
import { makeStyles, createMuiTheme } from '@material-ui/core/styles';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Box from '@material-ui/core/Box';
import ProgramListItems from './ProgramListItems';

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  content: {
    height: "70vh",
    padding: "0 !important",
    '&:last-child': {
      paddingBottom: "0 !important",
    },
  },
  programListItems: {
    position: 'relative',
    overflow: 'auto',
    maxHeight: "50vh",
  },
});

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

const ProgramListBody = () => {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return(
    <Container maxWidth="lg">
      <Card>
        <CardContent className={classes.content}>
            <Tabs value={value}
                  onChange={handleChange}
                  indicatorColor="primary"
                  textColor="primary"
                  variant="fullWidth"
                  aria-label="full width tabs example">
              <Tab label="All (1197)" {...a11yProps(0)} />
              <Tab label="Approved (4)" {...a11yProps(1)} />
              <Tab label="Pending (98)" {...a11yProps(2)} />
              <Tab label="Draft (887)" {...a11yProps(3)} />
              <Tab label="Canceled (12)" {...a11yProps(4)} />
              <Tab label="Back to Draft (12)" {...a11yProps(5)} />
            </Tabs>
          <TabPanel className={classes.programListItems} padding={0} value={value} index={0}>
            <ProgramListItems />
          </TabPanel>
          <TabPanel padding={0} value={value} index={1}>
            Item Two
          </TabPanel>
          <TabPanel padding={0} value={value} index={2}>
            Item Three
          </TabPanel>
          <TabPanel padding={0} value={value} index={3}>
            Item Three
          </TabPanel>
          <TabPanel padding={0} value={value} index={4}>
            Item Three
          </TabPanel>
          <TabPanel padding={0} value={value} index={5}>
            Item Three
          </TabPanel>
        </CardContent>
      </Card>
    </Container>
  );
}

export default ProgramListBody;
